body.script:
//
document.addEventListener('DOMContentLoaded', function() {
var toggleBtn = document.getElementById('theme-toggle');
var themeIcon = document.getElementById('theme-icon');

// Update the icon based on the active theme
function updateIcon(isLatte) {
if (themeIcon) {
themeIcon.className = isLatte ? 'ti ti-moon' : 'ti ti-sun';
}
}

// Check the current applied state
var isLatte = document.body.classList.contains('latte');
updateIcon(isLatte);

// Listen for click events to toggle the theme
if (toggleBtn) {
toggleBtn.addEventListener('click', function() {
document.body.classList.toggle('latte');
var currentlyLatte = document.body.classList.contains('latte');

// Save preference to browser storage
localStorage.setItem('portfolio-theme', currentlyLatte ? 'latte' : 'mocha');
updateIcon(currentlyLatte);
});
}
});
//
